import {MessageSquarePlus, Search} from 'lucide-react';
import type {ChatListItem} from './types';
import {motion} from 'framer-motion';

interface Props {
    items: ChatListItem[];
    onSelect: (id: string) => void;
    selectedId?: string | null;
}

export function ChatSidebar({items, onSelect, selectedId}: Props) {
    return (
        <div
            className="w-80 bg-white dark:bg-neutral-900 rounded-2xl shadow-md border border-gray-200 dark:border-neutral-800 flex flex-col overflow-hidden">
            {/* Header */}
            <div
                className="px-4 pt-4 pb-3 bg-white/70 dark:bg-neutral-900/70 backdrop-blur border-b border-gray-200 dark:border-neutral-800">
                <div className="flex items-center justify-between mb-3">
                    <h2 className="text-base font-semibold text-gray-900 dark:text-gray-100">Messages</h2>
                    <button
                        className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-neutral-800 text-gray-500"
                        title="New chat"
                    >
                        <MessageSquarePlus size={18}/>
                    </button>
                </div>
                <div className="relative">
                    <Search size={18} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400"/>
                    <input
                        placeholder="Search or start new chat"
                        className="w-full h-10 pl-10 pr-3 rounded-full bg-gray-100 dark:bg-neutral-800 text-sm focus:outline-none focus:ring-2 focus:ring-[#0084ff]/40"
                    />
                </div>
            </div>
            {/* List */}
            <div className="flex-1 overflow-y-auto p-2 space-y-1">
                {items.map((c) => (
                    <motion.button
                        key={c.id}
                        onClick={() => onSelect(c.id)}
                        initial={{opacity: 0, y: 6}}
                        animate={{opacity: 1, y: 0}}
                        className={`w-full flex items-center gap-3 p-3 rounded-xl transition text-left border ${
                            selectedId === c.id
                                ? 'bg-gray-50 dark:bg-neutral-800 border-gray-200 dark:border-neutral-700 shadow-sm'
                                : 'hover:bg-gray-50 dark:hover:bg-neutral-800 border-transparent'
                        }`}
                    >
                        <div className="relative">
                            <div
                                className="w-11 h-11 rounded-full bg-gradient-to-br from-purple-400 to-purple-600 flex items-center justify-center text-base ring-2 ring-white dark:ring-neutral-900">
                                {c.avatar ?? ''}
                            </div>
                            <span
                                className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-emerald-500 rounded-full ring-2 ring-white dark:ring-neutral-900"/>
                        </div>
                        <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between">
                                <p className="text-sm font-semibold text-gray-900 dark:text-gray-100 truncate">{c.name}</p>
                                <span className="text-[10px] text-gray-500">{c.lastMessageTime}</span>
                            </div>
                            <p className="text-xs text-gray-500 truncate">{c.lastMessage}</p>
                        </div>
                    </motion.button>
                ))}
            </div>
        </div>
    );
}

export default ChatSidebar;

