import {Paperclip, Send, Smile} from 'lucide-react';
import {useState} from 'react';

interface Props {
    onSend: (text: string) => void;
    accent?: string;
}

export function MessageInput({onSend, accent = '#0084ff'}: Props) {
    const [text, setText] = useState('');

    const handleSend = () => {
        const t = text.trim();
        if (!t) return;
        onSend(t);
        setText('');
    };

    return (
        <div
            className="h-16 border-t border-gray-200 dark:border-gray-700 px-4 flex items-center gap-2 bg-white/90 dark:bg-neutral-900/90 backdrop-blur rounded-b-2xl">
            <button className="p-2 hover:bg-gray-100 dark:hover:bg-neutral-800 rounded-full" aria-label="Emoji">
                <Smile size={20} className="text-gray-500"/>
            </button>
            <button className="p-2 hover:bg-gray-100 dark:hover:bg-neutral-800 rounded-full" aria-label="Attach">
                <Paperclip size={20} className="text-gray-500"/>
            </button>
            <input
                className="flex-1 h-10 px-4 rounded-full bg-gray-100 dark:bg-neutral-800 text-sm focus:outline-none focus:ring-2 focus:ring-[#0084ff]/40"
                placeholder="Type a message"
                value={text}
                onChange={(e) => setText(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            />
            <button
                onClick={handleSend}
                className="w-10 h-10 rounded-full text-white flex items-center justify-center shadow-sm hover:shadow transition"
                style={{background: accent}}
                aria-label="Send"
            >
                <Send size={18}/>
            </button>
        </div>
    );
}

export default MessageInput;

import {Smile, Send, Paperclip} from 'lucide-react';
import {useRef, useState} from 'react';

interface Props {
    onSend: (text: string) => void;
    accent?: string;
}

export function MessageInput({onSend, accent = '#0084ff'}: Props) {
    const [text, setText] = useState('');
    const [open, setOpen] = useState(false);
    const panelRef = useRef<HTMLDivElement | null>(null);

    const handleSend = () => {
        const t = text.trim();
        if (!t) return;
        onSend(t);
        setText('');
        setOpen(false);
    };

    const emojis = ['','','','','','','','','❤️',''];

    return (
        <div
            className="relative h-16 border-t border-gray-200 dark:border-gray-700 px-4 flex items-center gap-2 bg-white/90 dark:bg-neutral-900/90 backdrop-blur rounded-b-2xl">
            <div className="relative">
                <button className="p-2 hover:bg-gray-100 dark:hover:bg-neutral-800 rounded-full" aria-label="Emoji" onClick={() => setOpen(v=>!v)}>
                    <Smile size={20} className="text-gray-500"/>
                </button>
                {open && (
                    <div ref={panelRef} className="absolute bottom-12 left-0 z-10 w-56 bg-white dark:bg-neutral-900 border border-gray-200 dark:border-neutral-800 rounded-xl shadow-lg p-2 grid grid-cols-6 gap-2">
                        {emojis.map((e) => (
                            <button key={e} className="text-xl hover:bg-gray-100 dark:hover:bg-neutral-800 rounded" onClick={() => setText((t)=> t + e)}>
                                {e}
                            </button>
                        ))}
                    </div>
                )}
            </div>
            <button className="p-2 hover:bg-gray-100 dark:hover:bg-neutral-800 rounded-full" aria-label="Attach">
                <Paperclip size={20} className="text-gray-500"/>
            </button>
            <input
                className="flex-1 h-10 px-4 rounded-full bg-gray-100 dark:bg-neutral-800 text-sm focus:outline-none focus:ring-2 focus:ring-[#0084ff]/40"
                placeholder="Type a message"
                value={text}
                onChange={(e) => setText(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            />
            <button
                onClick={handleSend}
                className="w-10 h-10 rounded-full text-white flex items-center justify-center shadow-sm hover:shadow transition"
                style={{background: accent}}
                aria-label="Send"
            >
                <Send size={18}/>
            </button>
        </div>
    );
}

export default MessageInput;

import * as React from 'react';
import { cn } from '../../lib/utils';

export interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'default' | 'ghost' | 'outline';
  size?: 'sm' | 'md' | 'icon';
}

export const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant = 'default', size = 'md', ...props }, ref) => {
    const base = 'inline-flex items-center justify-center font-medium rounded-lg transition focus:outline-none focus-visible:ring-2 focus-visible:ring-blue-400 disabled:opacity-50 disabled:pointer-events-none';
    const variants = {
      default: 'bg-blue-500 text-white hover:bg-blue-600',
      ghost: 'bg-transparent hover:bg-gray-100 text-gray-700',
      outline: 'border border-gray-300 hover:bg-gray-50 text-gray-700',
    } as const;
    const sizes = {
      sm: 'h-8 px-3 text-sm',
      md: 'h-10 px-4 text-sm',
      icon: 'h-10 w-10',
    } as const;

    return (
      <button ref={ref} className={cn(base, variants[variant], sizes[size], className)} {...props} />
    );
  }
);
Button.displayName = 'Button';

import * as React from 'react';
import { cn } from '../../lib/utils';

export interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {}

export const Input = React.forwardRef<HTMLInputElement, InputProps>(({ className, ...props }, ref) => {
  return (
    <input
      ref={ref}
      className={cn('flex h-10 w-full rounded-lg border border-gray-300 bg-white px-3 text-sm shadow-sm placeholder:text-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-300', className)}
      {...props}
    />
  );
});
Input.displayName = 'Input';

export function cn(...classes: Array<string | false | null | undefined>) {
  return classes.filter(Boolean).join(' ');
}
